import React, { Component } from 'react'
import ReactMarkdown from 'react-markdown'
import styles from '../../styles/styles'

function flatten(text, child) {
    return typeof child === 'string'
      ? text + child
      : React.Children.toArray(child.props.children).reduce(flatten, text)
  }

  function HeadingRenderer(props) {
    var children = React.Children.toArray(props.children)
    var text = children.reduce(flatten, '')
    var slug = text.toLowerCase().replace(/\W/g, '-')
    return <h2 className={"text-2xl font-bold"}>Sobit Neupane</h2>
}

class BlogMain extends Component {
    constructor(props) {
        console.log(props)
      super(props)

      this.state = { terms: null }
    }

    componentDidMount() {
        const readmePath = require(`../../blogPosts/posts/${this.props.match.params.id}.md`);
        console.log(`../../blogPosts/posts/${this.props.match.params.id}.md`)
        fetch(readmePath).then((response) => response.text()).then((text) => {
            this.setState({ terms: text })
        })
    }


    render() {
        const input = '# This is a header\n\nAnd this is a paragraph'
        console.log(this.state.terms)
      return (
        <div className='blogContent' style={styles.blogContent}>
            <ReactMarkdown components = {{h1: HeadingRenderer}} ># Hello, *World*!</ReactMarkdown>
            <ReactMarkdown children={input}/>
        </div>
      )
    }
  }

  export default BlogMain
