import React, { Component } from 'react'
import ReactMarkdown from 'react-markdown'
import styles from '../../styles/styles'

function HeadingRenderer(props) {
    return <h1 className={"text-2xl text-center font-bold"} style={{paddingBottom:30}}>{props.children}</h1>
}

function ParagraphRenderer(props) {
    return <p style={{paddingBottom:20}}>{props.children}</p>
}

function anchorRenderer(props) {
    console.log(props)
    return <a href={props.href} style={{color: 'blue'}}>{props.children}</a>;
}

class BlogMain extends Component {
    constructor(props) {
      super(props)

      this.state = { terms: null }
    }

    componentDidMount() {
        import(`../../blogPosts/posts/${this.props.match.params.id}.md`).then(res=> { 
        fetch(res.default).then((response) => response.text()).then((text) => {
            this.setState({ terms: text })
        })
    })
    }


    render() {
      return (
        <div className='blogContent' style={styles.blogContent}>
            <ReactMarkdown 
                components = {{
                    h1: HeadingRenderer,
                    p: ParagraphRenderer,
                    a: anchorRenderer,
                }} 
                children={this.state.terms}
            />
        </div>
      )
    }
  }

  export default BlogMain
